import React, {Component} from 'react';

function Login() {
    const submit = (e) => {
        e.preventDefault();
        console.log(e.target[0].value);

        const login = async () => {
            const res = await fetch('https://localhost:4000/login', {method: 'post', 
                body: {
                    email: e.target[0].value,
                    password: e.target[1].value
                }
            });
            console.log(res);
            if(res.message === 'Success')
                alert('Login successful!');
            else
                alert('Invalid credentials!');
        }
    }

    return (
        <form onSubmit= {(e) => {submit(e)}} className="text-center" class="d-flex flex-column min-vh-100 justify-content-center align-items-center">
            <div class="mb-3">
                <label for="exampleInputEmail1" class="form-label" >
                    E-mail:
                    <input type="text" name="email" class="form-control" id="exampleInputEmail1" placeholder="name@example.com"/>
                </label>
            </div>
            <div class="mb-3">
                <label for="exampleInputPassword1" class="form-label">
                    Password:
                    <input type="password" name="password" class="form-control" id="exampleInputPassword1"/>
                </label>
            </div>
            <div class="mb-3">
                <input type="submit" value="Login" class="btn btn-primary" />
            </div>
        </form> 
    )
}

export default Login;
