import React, {Component} from 'react';

function Login() {
    const submit = (e) => {
        e.preventDefault();
        console.log(e.target);
    }

    return (
        <form className="text-center" class="d-flex flex-column min-vh-100 justify-content-center align-items-center">
                <label for="exampleInputEmail1" class="form-label" >
                    E-mail:
                    <input type="text" name="email" class="form-control" id="exampleInputEmail1" placeholder="name@example.com"/>
                </label>
                <label for="exampleInputPassword1" class="form-label">
                    Password:
                    <input type="password" name="password" class="form-control" id="exampleInputPassword1"/>
                </label>
                <input type="submit" value="Login" class="btn btn-primary" onClick={(e) => {submit(e)}}/>
        </form> 
    )
}

export default Login;
